mapper.select:
SELECT

FROM (
SELECT l.*,h.HOSPITAL_NAME,u.USER_NAME FROM FBS_LIVE_MESSAGE l
LEFT JOIN FBS_USER u ON u.ID =l.USER_ID
LEFT JOIN fbs_hospital_information h ON u.HOSPITAL_ID = h.ID
) a
WHERE a.STATUS = ${map.status}

	AND a.LIVE_ID = ${map.id}


	AND a.USER_ID = ${map.userId}


	AND a.MTYPE = ${map.mtype}


	AND a.CREATOR_ID IN (${map.strCreatId})

order by a.ID desc

SELECT count(1) FROM (SELECT l.CREATED_TIME,u.USER_NAME,l.CONTENT FROM FBS_LIVE_MESSAGE l LEFT JOIN FBS_USER u ON u.ID =l.USER_ID WHERE l.STATUS = ${map.status}
AND u.USER_NAME LIKE '%${map.userName}%'
) b

SELECT COUNT(1) FROM FBS_LIVE_MESSAGE WHERE 1=1
AND LIVE_ID = #{liveId}
AND USER_ID = #{userid} AND STATUS = #{status} AND MTYPE = 2
