mapper.select:
select ID,TITLE,START_TIME,END_TIME,LIVE_INTRODUCTION,USER_ID,PHONE,EMAIL,DEPARTMENT_NAME ,HOSPITAL_WEBSITE,APPROVAL_TIME, if(ISDEL='1','3',APPROVAL_STATUS) APPROVAL_STATUS ,PLAY_STATUS,DEPARTMENT_ID,CONF_ID ,ISGENERATED,MEETING_STATUS,CREATOR_ID,CREATED_TIME,UPDATE_ID,UPDATED_TIME,FILE_NAME,FILE_PATH,PICTURE_NAME,PICTURE_PATH,NAME,ISDEL FROM ( SELECT a.*,department.DEPARTMENT_NAME DEPARTMENT_NAME,c.HOSPITAL_NAME NAME from FBS_LIVE_BROADCAST a INNER JOIN FBS_USER b ON a.USER_ID = b.ID INNER JOIN fbs_department department ON department.ID = a.DEPARTMENT_ID INNER JOIN FBS_HOSPITAL_INFORMATION c ON b.HOSPITAL_ID=c.ID UNION SELECT a.*,department.DEPARTMENT_NAME DEPARTMENT_NAME,b.USER_NAME NAME from FBS_LIVE_BROADCAST a INNER JOIN FBS_USER b ON a.USER_ID = b.ID INNER JOIN FBS_USER_ROLE c ON b.ID=c.USER_ID INNER JOIN fbs_department department ON department.ID = a.DEPARTMENT_ID INNER JOIN FBS_ROLE d ON d.ID = c.ROLE_ID WHERE d.ID=1 ) e where 1=1
and USER_ID =#{userId}
and NAME =#{name}
and TITLE like '%${title}%'
and START_TIME >= #{startTime}
and START_TIME <=#{endTime}
and APPROVAL_STATUS =#{approvalStatus}
and CREATED_TIME >= #{timeBeginLive}
and CREATED_TIME <= #{timeEndLive}
ORDER BY CREATED_TIME DESC,START_TIME DESC

    	select count(*) from FBS_LIVE_BROADCAST
    	where 1=1

    	and USER_ID =#{userId}


			and TITLE like '%${title}%' 


			and START_TIME >= #{startTime}


			and START_TIME <=#{endTime}


			and APPROVAL_STATUS =#{approvalStatus}


			and CREATED_TIME >= #{timeBeginLive}


			and CREATED_TIME <= #{timeEndLive}

mapper.delete:
delete from FBS_LIVE_BROADCAST
where 1=1
and ID =#{id,jdbcType=INTEGER}

mapper.update:
update FBS_LIVE_BROADCAST
set APPROVAL_STATUS = 0
where ID = #{id,jdbcType=INTEGER}
