mapper.update:
	        update FBS_LIVE_FILE   set   
               UPLOAD_STATUS = #{status},
               FILE_SIZE = #{fileSize}
            where      
               CONF_ID = #{confId} and HOSPITAL_ID = #{hospitalId} and CODEC_ID = #{codecId}  
               and FILE_NAME = #{filename}

	        update FBS_LIVE_FILE   set   
               FILE_NAME = #{filename}
            where      
               id = #{id}

mapper.select:
SELECT DISTINCT live.ID AS liveid FROM FBS_LIVE_PARTICIPANT flp INNER JOIN fbs_live_broadcast live ON live.ID = flp.LIVE_ID INNER JOIN fbs_live_file file on live.CONF_ID = file.CONF_ID INNER JOIN fbs_hospital_information hospital ON hospital.ID = flp.HOSPITAL_ID and file.HOSPITAL_ID = hospital.ID INNER JOIN fbs_live_codec CODE ON CODE.ID = file.CODEC_ID where live.CONF_ID is not null and (file.OLDFILE_STATUS = '0' or file.STOREFILE_STATUS = '0' )
and live.TITLE like concat('%', #{title}, '%')
and hospital.id = #{hospital}
and file.upload_status = #{uploadstatus}
and live.start_time between ${'"+#{timeBegin}+"'} and ${'"+#{timeEnd}+"'}

SELECT case when LENGTH (substring_index(file.FILE_NAME,'#', -1)) < '7' then CONCAT(live.TITLE, '(',hospital.HOSPITAL_NAME,')',DATE_FORMAT( live.START_TIME,'%Y%m%d'),'#' ,substring_index(file.FILE_NAME,'#', -1) ) else CONCAT(live.TITLE, '(',hospital.HOSPITAL_NAME,')',DATE_FORMAT( live.START_TIME,'%Y%m%d') ,'.mp4' ) end as displayName, file.id as id, file.FILE_PATH filepath, file.FILE_NAME as filename, file.UPLOAD_STATUS as uploadstatus, file.STOREFILE_STATUS as storefilestatus, file.OLDFILE_STATUS AS oldfilestatus, file.CODEC_ID codecid, hospital.ID AS hospitalid, hospital.HOSPITAL_NAME as hospitalname, hospital.BUCKET as BUCKET, live.id as liveid, live.CONF_ID AS confid, live.START_TIME as starttime, live.title as title, codec.IP as ip, codec.PORT as port, codec.USERNAME as username, codec.PASSWORD as password FROM FBS_LIVE_PARTICIPANT flp INNER JOIN fbs_live_broadcast live ON live.ID = flp.LIVE_ID INNER JOIN fbs_live_file file on live.CONF_ID = file.CONF_ID INNER JOIN fbs_hospital_information hospital ON hospital.ID = flp.HOSPITAL_ID and file.HOSPITAL_ID = hospital.ID INNER JOIN fbs_live_codec codec ON codec.ID = file.CODEC_ID where live.ID is not null and live.CONF_ID is not null and live.ID in (
#{item.liveid}
)
and hospital.id = #{hospital}
ORDER BY live.ID,hospital.ID
